import React, { useState, useEffect } from "react";
import { connect } from 'react-redux';
import {
    withRouter
} from 'react-router-dom';
// import { setCustomerName, setCustomerMail, setCustomerRoom } from '../Store/actions'
import socketIoClient from 'socket.io-client';


const mapStateToProps = (state) => {
    return {
        customer: state.customerReducer.customer
    };
}
const mapDispatchToProps = (dispatch) => ({
})


const ENDPOINT = "http://localhost:3400";

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(function Messages(props) {
    const { customer, history } = props

    const [allMessages, setAllMessages] = useState([]);
    const [messageTxt, setMessageTxt] = useState('');
    const [messageDt, setMessageDt] = useState('');
    debugger;

    const conId = history.location.pathname.split('/')[2];
    const socket = socketIoClient(ENDPOINT);

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    var requestOptions = {
        method: 'GET',
        headers: myHeaders,
    };


    useEffect(() => {
        console.log('useEffect');
        fetch(`http://localhost:3400/getMessagesByConversationId/${conId}`, requestOptions)
            .then(response => response.json())
            .then(response => {
                debugger;
                console.log(allMessages);
                setAllMessages(response);
                console.log(allMessages);
            })
            .catch(error => console.log('error', error));

        socket.on("connection", () => {
            console.log(socket);
            console.log("connect");
        })

        socket.on("message", (message) => {
            console.log("PPPPPPPPPPPPPPPPPPP" + message);
            let messagesTemp = [...allMessages];
            messagesTemp.push(message);
            setAllMessages(messagesTemp);
            // console.log("connect");
        })

    }, [])

    function backToConversations() {
        history.push('/Customer')
    }

    function sendMessage() {

        debugger;
        const message = {
            messageText: messageTxt,
            messageDate: messageDt,
            messageWriter: customer.customerName,
            conId: conId,
            customerRoom: customer.customerRoom
        }
        socket.emit('sendMessage', message);


    }

    return (
        <div >
            <h1 >Welcome To Messages</h1>
            <p>current customer: mail: {customer.customerMail}, name: {customer.customerName}, room: {customer.customerRoom} </p>
            <form className="rounded-top" style={{ backgroundColor: "white", boxShadow: "5px 5px 2px grey", marginLeft: "70%", width: "300px", height: "450px", border: "black 2px solid" }}>
                <div style={{ backgroundColor: "black", height: "40px", color: "white" }}>
                    <p className="mx-4 mb-4" style={{ display: "inline-block" }}>Messages</p>
                    <svg onClick={backToConversations} style={{ cursor: "pointer", display: "inline-block", marginLeft: "30%" }} xmlns="http://www.w3.org/2000/svg" height="48" width="48" viewBox="0 0 48 48">
                        <g>
                            <path id="path1" transform="rotate(0,24,24) translate(11,11.9749998450279) scale(0.8125,0.8125)  " fill="#007BFF" d="M25.399994,10.400033L32,16.60005 25.399994,23.000019 25.399994,18.599991 14,18.599991 14,14.800061 25.399994,14.800061z M0,0L22.200012,0 22.200012,11.800058 18.800018,11.800058 18.800018,3.2000146 3.2000122,3.2000146 3.2000122,26.400047 18.800018,26.400047 18.800018,21.400042 22.200012,21.400042 22.200012,29.6 0,29.6z" />
                        </g>
                    </svg>
                </div>






                {allMessages.messages?.length > 0 ?
                    <div style={{ overflow: "scroll", height: "420px", width: "300px" }}>
                        {


                            allMessages.messages.map((item, index) => (
                                <div key={index} style={{ padding: "10px" }}
                                >
                                    {item.messageWriter !== 'Admin' ?
                                        <div style={{ direction: "rtl" }}  >
                                             <svg style={{display:"inline-block"}} xmlns="http://www.w3.org/2000/svg" height="30" width="30" viewBox="0 0 48 48">
                                                        <g>
                                                            <ellipse cy="24" cx="24" ry="24" rx="24" fill="#71C5CD" />
                                                            <path id="path1" transform="rotate(0,24,24) translate(11.3380002975464,11) scale(0.8125,0.8125)  " fill="#0E6C7D" d="M8.3560181,18.363037C10.371002,19.880005 12.873993,20.779053 15.584991,20.779053 18.295013,20.779053 20.799011,19.880005 22.813995,18.363037 27.524017,18.955994 31.167999,22.973022 31.167999,27.843994L31.167999,32 0,32 0,27.843994C0,22.973022,3.6459961,18.955994,8.3560181,18.363037z M15.584991,0C20.403992,0 24.311005,3.90802 24.311005,8.7280273 24.311005,13.548035 20.403992,17.454041 15.584991,17.454041 10.765015,17.454041 6.8580017,13.548035 6.8580017,8.7280273 6.8580017,3.90802 10.765015,0 15.584991,0z" />
                                                        </g>
                                                    </svg>
                                            <p className="rounded-right" style={{display:"inline-block", backgroundColor: "lightblue", padding: "3px" }}> {item.messageText}</p>
                                            {customer.customerName !== 'Admin' ?
                                                <div>
                                                    <div></div>
                                                    <p>me * {item.messageDate}</p>
                                                </div>
                                                :
                                                <p>{customer.customerName} * {item.messageDate}</p>
                                            }
                                            {/* <p>{item.messageDate}</p> */}
                                        </div>
                                        :
                                        <div style={{ direction: "ltr" }}>
                                            <p style={{ backgroundColor: "lightgray", padding: "3px" }} className="rounded-right">{item.messageText}</p>
                                            {customer.customerName !== 'Admin' ?
                                                <p>support * {item.messageDate}</p>
                                                :
                                                <p>me * {item.messageDate}</p>
                                            }
                                            {/* <p>{item.messageDate}</p> */}
                                        </div>
                                    }


                                </div>
                            ))
                        }
                    </div>
                    :
                    <>
                        <p>There is no messages yet, you can be the first!!!</p>
                    </>
                }

                <div id="inputDiv" className="mx-1 " >
                    {/* {
                       
                        document.addEventListener('keydown', function (event) {
                            if (event.code === 'Enter') {
                                debugger;
                                // alert("you pressed enter");
                                sendMessage();
                            }
                        })
                    } */}
                    <textarea type="text" id="messTxt" className="form-control" placeholder="send a message" onChange={(e) => {
                        debugger;
                        //  let txt = document.getElementById('messTxt');
                        setMessageTxt(e.target.value)
                        let today = new Date();
                        let date = today.getDate() + '-' + (today.getMonth() + 1) + '-' + today.getFullYear() + '   ' + today.getHours() + ':' + today.getMinutes();

                        setMessageDt(date)
                    }} />
                    <button className="btn btn-primary" onClick={sendMessage}>
                        send
                    </button>
                </div>
            </form>

        </div >
    );
}))
