import React,{useState} from "react";

export function Login(){
    const [userName,setUserName]=useState('')
    const [userPassword,setUserPassword]=useState('')

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    function login() {
        debugger;
        const customer = {
            "name": userName,
            "password": userPassword
        }
        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: JSON.stringify(customer),
        };

        fetch("http://localhost:3400/createCustomer", requestOptions)
            .then(response => response.json())
            .catch(error => console.log('error', error));

    }
    return (
        <div className="container">
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <div id="loader4">
                            <span className="loader loader-1"></span>
                            <span className="loader loader-2"></span>
                            <span className="loader loader-3"></span>
                            <span className="loader loader-4"></span>
                        </div>
                    </div>
                </div>
                <br /><br />
            </div>
            <div className="row">
                <div className="col-3">

                </div>
                <div className="col-6">
                    <form>
                        <h1>Login</h1>
                        <div className="mb-3">
                            <label htmlFor="name" className="form-label">Name</label>
                            <input type="text" className="form-control" id="name"  />
                        </div>


                        <div className="mb-3">
                            <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                            <input type="password" className="form-control" id="exampleInputPassword1" ref={reftoPassword} />
                        </div>

                    </form>
                    <button className="btn btn-primary" onClick={login}>Login</button>

                </div>

            </div >

        </div >


    )
}

