import { produce } from 'immer';
// import messages from '../components/messages';



const initialState = {
    conversation:{
        conversationId:'',
        customerName : '',
        messages : []
    }
    // ,
    // message:{
    //     messagesText='',
    //     messageDate='',
    //     messageWriter=''
    // }
};

export default produce((state, action) => {
    switch (action.type) {
        case 'SET_CONVERSATION_ID':
            state.conversation.conversationId = action.payload;
            break;
        case 'SET_CUSTOMER_NAME':
            state.conversation.customerName = action.payload;
            break;
    }
}, initialState)
