//NOTE
import { produce } from 'immer';

const initialState = {
    message: {
        allMessages: [],
    }
      // socket: io.connect('http://localhost:4000/', {
    //     transports: ['websocket']
    // }),
};


export default produce((state, action) => {
    switch (action.type) {
        case 'SET_ALL_MESSAGES':
            state.message.allMessages = action.payload;
            break;
    }
}, initialState)
