import React,{useState} from "react";
import {
    withRouter
} from 'react-router-dom'

export default withRouter( function Login(props) {
    const { history } = props
    const [customerName,setCustomerName]=useState('');
    const [customerMail,setCustomerMail]=useState('');
    const [customers,setCustomers]=useState([]);
    const [existCustomer,setExistCustomer]=useState(false);

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    function login() {
        debugger;
        const customer = {
            "customerName": customerName,
            "customerMail": customerMail
        }

        var requestOptions1 = {
            method: 'GET',
            headers: myHeaders,
            // body: JSON.stringify(customer),
        };

        fetch("http://localhost:3400/getAllCustomers", requestOptions1)
            .then(response => response.json())
            .then(response=>{
                deb
                setCustomers(response)
            })
            .catch(error => console.log('error', error));

for (let i = 0; i < customers.length.length; i++) {
   if(customers[i].customerMail==customer.customerMail)
   setExistCustomer(true); 
}

if(!existCustomer){


        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: JSON.stringify(customer),
        };

        fetch("http://localhost:3400/createCustomer", requestOptions)
            .then(response => response.json())
            .catch(error => console.log('error', error));

            if(customerName==='Admin')
            {
                history.push('/Admin')
            }
            else{
                history.push('/Customer')

            }
        }
        else{
            alert('this customer is already exist!')
        }
    }
    return (
        <div className="container">
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <div id="loader4">
                            <span className="loader loader-1"></span>
                            <span className="loader loader-2"></span>
                            <span className="loader loader-3"></span>
                            <span className="loader loader-4"></span>
                        </div>
                    </div>
                </div>
                <br /><br />
            </div>
            <div className="row">
                <div className="col-3">

                </div>
                <div className="col-6">
                    <form>
                        <h1>Login</h1>
                        <div className="mb-3">
                            <label htmlFor="name" className="form-label">Name</label>
                            <input type="text" className="form-control" id="name"  onChange={(e)=>setCustomerName(e.target.value)}/>
                        </div>


                        <div className="mb-3">
                            <label htmlFor="exampleInputPassword1" className="form-label">Mail</label>
                            <input type="text" className="form-control" id="exampleInputPassword1" onChange={(e)=>setCustomerMail(e.target.value)} />
                        </div>

                    </form>
                    <button className="btn btn-primary" onClick={login}>Login</button>

                </div>

            </div >

        </div >


    )
})

