import React from 'react';
import {
    Switch,
    Route,
    Link,
    withRouter
    // Redirect
} from 'react-router-dom';
import Support from './components/support';
import Customer from './components/customer';
import Messages from './components/messages';
import Login from './components/login';
import { connect } from 'react-redux'

const mapStateToProps = (state) => {
    return {
        customer: state.customerReducer.customer
    };
}
const mapDispatchToProps = (dispatch) => ({
    // setConfirm: (con) => dispatch(setConfirmPayment(con)),

})

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(function Customer(props) {

    const { customer, history } = props
    return (
        <div style={{backgroundColor:"rgb(231, 231, 250)"}}>
                        <p>current customer: mail: {customer?.customerMail}, name: {customer.customerName}, room: {customer.customerRoom} </p>
            <Menu />
            <Switch>
                {customer?.customerName!==''?
                <Route path="/Admin">
                    <Support />
                </Route>
                :
                <Route path="/">
                    <Login />
                </Route>
}
{customer?.customerName!==''?
                <Route path="/Customer">
                    <Customer />
                </Route>
                 :
                 <Route path="/">
                     <Login />
                 </Route>
 }
 {customer?.customerName!==''?
                <Route path="/Messages/:conId">
                    <Messages />
                </Route>
                :
                <Route path="/">
                    <Login />
                </Route>
}
                <Route path="/Login">
                    <Login />
                </Route>
                <Route path="/">
                    <Login />
                </Route>
            </Switch>
        </div>
    );
}
))

function Menu() {
    return (
        <>

            <nav className="navbar navbar-expand-lg navbar-light bg-light" style={{ direction: "rtl" }}>
                {/* <div className="navbar-nav">
                    <Link className="nav-link active" aria-current="page" to="/Admin">Support</Link>
                </div>
                <div className="navbar-nav">
                    <Link className="nav-link active" aria-current="page" to="/Customer">Customers</Link>
                </div> */}
                <div className="navbar-nav">
                    <Link className="nav-link active" aria-current="page" to="/Login">Login</Link>
                </div>
                {/* <div className="navbar-nav">
                    <Link className="nav-link active" aria-current="page" to="/">Home Page</Link>
                </div> */}

            </nav>
        </>
    );
}
