import {
    withRouter
} from 'react-router-dom'
import { connect } from 'react-redux'
import { setCustomerName, setCustomerMail, setCustomerRoom } from '../Store/actions'

const mapStateToProps = (state) => {
    return {
        customer: state.customerReducer.customer
    };
}
const mapDispatchToProps = (dispatch) => ({
    setCustomerName: (name) => dispatch(setCustomerName(name)),
    setCustomerMail: (mail) => dispatch(setCustomerMail(mail)),
    setCustomerRoom: (room) => dispatch(setCustomerRoom(room)),
    // setConversations: (name) => dispatch(setCustomerName(name))
})

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(function Login(props) {

    const { customer, setCustomerName, setCustomerMail, setCustomerRoom, history } = props
    // const [customerName, setCustomerName] = useState('');
    // const [customerMail, setCustomerMail] = useState('');
    const [customers, setCustomers] = useState([]);
    const [existCustomer, setExistCustomer] = useState(false);
    const [loginCustomer, setLoginCustomer] = useState(false);

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    function login() {
        debugger;
        const customerDetails = {
            "customerName": customer.customerName,
            "customerMail": customer.customerMail,
            "customerRoom": customer.customerRoom
        }

        var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            // body: JSON.stringify(customerDetails),
        };
        
        fetch("http://localhost:3400/getAllCustomers", requestOptions)
            .then(response => response.json())
            .then(response => {
                setCustomers([...customers,response]);

                // setCustomers(response);
                debugger;
                customers.map((item, index) => {
                    debugger;
                    if (item.customerMail === customer.customerMail && item.customerName === customer.customerName && item.customerRoom === customer.customerRoom)
                        setLoginCustomer(true);
                    if (item.customerMail === customer.customerMail) {
                        alert("this mail has been used");
                        setExistCustomer(true);
                    }
                    if (item.customerName === customer.customerName) {
                        alert("this name has been used");
                        setExistCustomer(true);
                    }
                    if (item.customerRoom === customer.customerRoom) {
                        alert("this room has been used");
                        setExistCustomer(true);
                    }
                })
            }) 
            .catch(error => console.log('error', error));

            debugger;
if(loginCustomer)
