import React, { useState, useEffect } from "react";
import { connect } from 'react-redux';
import {
    withRouter
} from 'react-router-dom';
import { setCustomerName, setCustomerMail, setCustomerRoom } from '../Store/actions'
import socketIoClient from 'socket.io-client';


const mapStateToProps = (state) => {
    return {
        customer: state.customerReducer.customer
    };
}
const mapDispatchToProps = (dispatch) => ({
    setCustomerName: (name) => dispatch(setCustomerName(name)),
    setCustomerMail: (mail) => dispatch(setCustomerMail(mail)),
    setCustomerRoom: (room) => dispatch(setCustomerRoom(room)),
    // setConversations: (name) => dispatch(setCustomerName(name))
})


const ENDPOINT = "http://localhost:3400";

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(function Messages(props) {
    const { customer, setCustomerName, setCustomerMail, setCustomerRoom, history } = props

    const [allMessages, setAllMessages] = useState([]);
    const [messageTxt, setMessageTxt] = useState('');
    const [messageDt, setMessageDt] = useState('');
    debugger;

    const conId = history.location.pathname.split('/')[2];
    const socket = socketIoClient(ENDPOINT);

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
    var requestOptions = {
        method: 'GET',
        headers: myHeaders,
    };


    useEffect(() => {

        fetch("http://localhost:3400/getMessagesByConversationId/:conId", requestOptions)
            .then(response => response.json())
            .then(response => {
                debugger;
                setAllMessages(response);
            })
            .catch(error => console.log('error', error));

        socket.on("connection", () => {
            console.log(socket);
            console.log("connect");
        })

        socket.on("message", (message) => {
            console.log(message);
            let messagesTemp = [...allMessages];
            messagesTemp.push(message);
            setAllMessages(messagesTemp);
            // console.log("connect");
        })

    }, [])

function backToConversations(){
    history.push('/Customer')
}

    function sendMessage() {

        debugger;
        const message = {
            messageText: messageTxt,
            messageDate: messageDt,
            messageWriter: customer.setCustomerName
        }
        socket.emit('sendMessage', message);

        // var requestOptions = {
        //     method: 'POST',
        //     headers: myHeaders,
        //     body: JSON.stringify(message),
        // };

        // fetch("http://localhost:3400/newMessage", requestOptions)
        //     .then(response => response.json())
        //     .catch(error => console.log('error', error));
    }

    return (
        <div>
            <h1 >Welcome To Messages</h1>

            <form className="rounded-top" style={{ boxShadow:"5px 5px 2px grey", marginLeft: "70%", width: "25%", height: "450px", border: "black 2px solid" }}>
                <div style={{ backgroundColor: "black", height: "40px", color: "white"}}>
                    <p className="mx-4 mb-4" style={{display:"inline-block"}}>Messages</p>
                    <svg onClick={backToConversations} style={{cursor: "pointer", display:"inline-block",marginLeft:"45%"}} xmlns="http://www.w3.org/2000/svg" height="48" width="48" viewBox="0 0 48 48">
                        <g>
                            <path id="path1" transform="rotate(0,24,24) translate(11,11.9749998450279) scale(0.8125,0.8125)  " fill="#007BFF" d="M25.399994,10.400033L32,16.60005 25.399994,23.000019 25.399994,18.599991 14,18.599991 14,14.800061 25.399994,14.800061z M0,0L22.200012,0 22.200012,11.800058 18.800018,11.800058 18.800018,3.2000146 3.2000122,3.2000146 3.2000122,26.400047 18.800018,26.400047 18.800018,21.400042 22.200012,21.400042 22.200012,29.6 0,29.6z" />
                        </g>
                    </svg>
                </div>

                {/* {allMessages !== undefined ?
                    <>
                        {
                            allMessages.map((item, index) => (
                                <div key={index}>
                                    {item.messageWriter !== 'Admin' ?
                                        <div style={{ marginLeft: "10%" }}>
                                        {custome.customerName!=='Admin?
                                          <p>me:</p>
                                          :
                                            <p>{custome.customerName}</p>

                                        }
                                        <p style={{ backgroundColor: "lightblue" }}> {item.messageText}</p>
                                            <p>{item.messageDate}</p>
                                            <p>me:</p>
                                        </div>
                                        :
                                        <div style={{ marginRight: "10%" }}>
                                        {custome.customerName!=='Admin?
                                          <p>support:</p>
                                          :
                                            <p>me:</p>
                                        }    
                                        <p style={{ backgroundColor: "lightgray" }}>support: {item.messageText}</p>
                                            <p>{item.messageDate}</p>
                                        </div>
                                    }


                                </div>
                            ))
                        }
                    </>
                    :
                    <>
                        <p>There is no conversations yet, to start - press on the icon above</p>
                    </>
                } */}

                <div id="inputDiv" className="mx-1 " style={{ marginTop: "99%" }}>
                    {
                        document.addEventListener('keydown', function (event) {
                            if (event.code == 'Enter') {
                                console.log("you pressed enter");
                            }
                        })
                    }
                    <input type="text" id="messTxt" className="form-control" placeholder="enter your message" onChange={(e) => {
                        debugger;
                        //  let txt = document.getElementById('messTxt');
                        setMessageTxt(e.target.value)
                        let today = new Date();
                        let date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
                        setMessageDt(date)
                    }} /><button className="btn btn-primary" onClick={sendMessage}>
                        send
                    </button>
                    {/* <textarea className="form-control" placeholder="Leave your message here" ></textarea> */}
                </div>
            </form>

        </div >
    );
}))
